map = []
address = []
token = ""
for each in text['result']['records']:
    id = each['_id']
    name = each['nome']
    id_address = each['endereco']
    id_address = id_address.replace(' ', '+')
    url = "https://maps.googleapis.com/maps/api/geocode/json?address=" + id_address + "&key="+token
    response = requests.get(url)
    data = response.json()
    obj_address = {'id': id,
                   'address': id_address,
                   'address_components': {},
                   'lat': None,
                   'lng': None}
    try:
        obj_address['lat'] = data['results'][0]['geometry']['location']['lat']
        obj_address['lng'] = data['results'][0]['geometry']['location']['lng']
        obj_address['address_components'] = data['results'][0]['address_components']
    except:
        pass
    map.append({'id': id,
                'name': name,})
    print(obj_address)
    address.append(obj_address)




with open('cordenates', 'w') as outfile:
    json.dump(address, outfile)

with open('map', 'w') as outfile:
    json.dump(map, outfile)

